import CryptoJS from 'react-native-crypto-js';

const getepayPortal = (data, config) => {
  const JsonData = JSON.stringify(data);

  const ciphertext = CryptoJS.AES.encrypt(JsonData, config["Getepay Key"], {
    iv: config["Getepay IV"],
  }).toString().toUpperCase();

  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");

  const raw = JSON.stringify({
    mid: data.mid,
    terminalId: data.terminalId,
    req: ciphertext,
  });

  const requestOptions = {
    method: "POST",
    headers: myHeaders,
    body: raw,
    redirect: "follow",
  };

  fetch(config["Getepay Url"], requestOptions)
    .then((response) => response.json())
    .then((result) => {
      console.log("900", result.data);
      const resultobj = result;
      const responseurl = resultobj.response;
      const decryptedData = CryptoJS.AES.decrypt(responseurl, config["Getepay Key"], {
        iv: config["Getepay IV"],
      }).toString(CryptoJS.enc.Utf8);
      const dataitem = JSON.parse(decryptedData);
      // You may need to handle the redirection differently in React Native
      // e.g., use Navigation or Linking API
      console.log("Payment URL:", dataitem.paymentUrl);
    })
    .catch((error) => console.log("error", error));
};

export default getepayPortal;
